<template>

  <div class = "honeycomb">
    <div class = "honeycomb-number">
      <div
        class="honeycomb-number-list"
        :class = "{ 'ready': !action, 'finish': action == 'finish' }"
        :style = "{ transform: 'translateY(' +  translateY + 'px)'}"
        ref = "list">
        <span
          v-for = "(number, index) in numbers"
          :key = "index">
          {{ number }}
        </span>
      </div>
    </div>
  </div>

</template>

<script>
export default {
  props: [ 'stopNumber', 'delay', 'status' ],
  data: function() {
    return {
      numbers: [0, 9],
      dateStart: undefined,
      translateY: undefined,
      timerInterval: undefined,
      action: false
    }
  },
  watch: {

    status: function (action) {

      if (action == 'start') {
        setTimeout(() => {
          this.$set(this, 'dateStart', Date.now());
          this.$set(this, 'action', 'start');

          this.generateArray();
          this.timerInterval = setInterval(() => {

            this.generateArray();
          }, 30);

        }, this.delay);
      }

      if (action == 'stop') {
        clearInterval(this.timerInterval);
      }
    }

  },
  methods: {

    generateArray: function () {
      let numbers = this.numbers;

      numbers.push(this.numbers[this.numbers.length - 1] === 0 ? 9 : this.numbers[this.numbers.length - 1] - 1);

      this.$set(this, 'numbers', numbers);

      if (this.translateY === undefined) {
        this.$set(this, 'translateY', 0);

      } else {
        this.$set(this, 'translateY', ('-' + (78 * (this.numbers.length - 3))));

      }

      if (Date.now() - this.dateStart >= 600) {
        if (this.numbers[this.numbers.length - 3] == Number(this.stopNumber)) {
          clearInterval(this.timerInterval);

          this.$set(this, 'action', 'finish');

          this.$anime({
            targets: this.$refs.list,
            translateY: Number(this.translateY) + 7,
            duration: 100,
            direction: 'alternate',
            loop: false,
            easing: 'spring(1, 80, 10, 0)'
          });
        }
      }
    }

  }
}
</script>

<style>

.honeycomb-number span {
  flex: none;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 78px;
  font-size: 65px;
  font-weight: bold;
  color: #fff;
  line-height: 1;
}

.honeycomb-number-list {
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  height: 100%;
  position: relative;
  transition: transform .3s;
  transition-timing-function: cubic-bezier(.29, .01, .02, .99);
  transform: translateY(100%);
}

.honeycomb-number-list.ready {
  opacity: 0;
}

.honeycomb-number-list.finish {
  transition: none;
  padding-top: 19px;
}

.honeycomb-number {
  height: 133px;
  width: 118px;
  clip-path: polygon(59px 120px, 105px 93px, 104px 42px, 58px 15px, 12px 42px, 12px 92px);
  overflow: hidden;
  background: url('data:image/png;base64,') no-repeat center;
  background-size: contain;
}

.honeycomb {
  background: url('data:image/png;base64,') no-repeat center;
  background-size: contain;
}

</style>
